  { id: 1, name: "Ë´èË®™ÊπñÁ•≠Êπñ‰∏äËä±ÁÅ´Â§ß‰ºö" },
  { id: 2, name: "ÂñÑÂÖâÂØ∫Âæ°ÈñãÂ∏≥" },
  { id: 3, name: "ÊùæÊú¨„Åº„Çì„Åº„Çì" }
];

export default function FestivalPage() {
  const [comments, setComments] = useState({});
  const [ratings, setRatings] = useState({});

  const handleComment = (id, text) => {
    setComments(prev => ({ ...prev, [id]: [...(prev[id] || []), text] }));
  };

  const handleRate = (id, rating) => {
    setRatings(prev => ({ ...prev, [id]: rating }));
  };

  return (
    <div className="container">
      <h1>üéÜ Èï∑ÈáéÁúå„ÅÆ„ÅäÁ•≠„Çä üéÜ</h1>
      {festivals.map(festival => (
        <div key={festival.id} className="card">
          <h2>{festival.name}</h2>

          <div className="star-row">
            <StarRating
              key={festival.id}
              onRate={(rating) => handleRate(festival.id, rating)}
            />
          </div>
          <p>ÁèæÂú®„ÅÆË©ï‰æ°: {ratings[festival.id] || "Êú™Ë©ï‰æ°"}</p>

          <form
            onSubmit={e => {
              e.preventDefault();
              const text = e.target.elements.comment.value;
              if (text) {
                handleComment(festival.id, text);
                e.target.reset();
              }
            }}
          >
            <input type="text" name="comment" placeholder="„Ç≥„É°„É≥„Éà„ÇíÊõ∏„Åè..." required />
            <button type="submit">ÊäïÁ®ø</button>
          </form>

          <ul>
            {(comments[festival.id] || []).map((c, i) => (
              <li key={i}>üí¨ {c}</li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
}
