import React, { useState } from "react";
import StarRating from "./StarRating";

const festivals = [
  { id: 1, name: "諏訪湖祭湖上花火大会" },
  { id: 2, name: "善光寺御開帳" },
  { id: 3, name: "松本ぼんぼん" }
];

export default function FestivalPage() {
  const [comments, setComments] = useState({});
  const [ratings, setRatings] = useState({});

  const handleComment = (id, text) => {
    setComments(prev => ({ ...prev, [id]: [...(prev[id] || []), text] }));
  };

  const handleRate = (id, rating) => {
    setRatings(prev => ({ ...prev, [id]: rating }));
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>長野県のお祭り</h1>
      {festivals.map(festival => (
        <div key={festival.id} style={{ border: "1px solid #ccc", margin: "10px", padding: "10px" }}>
          <h2>{festival.name}</h2>
          <StarRating onRate={rating => handleRate(festival.id, rating)} />
          <form onSubmit={e => {
            e.preventDefault();
            const text = e.target.elements.comment.value;
            if (text) {
              handleComment(festival.id, text);
              e.target.reset();
            }
          }}>
            <input type="text" name="comment" placeholder="コメントを書く..." required />
            <button type="submit">投稿</button>
          </form>
          <ul>
            {(comments[festival.id] || []).map((c, i) => (
              <li key={i}>{c}</li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
}
