// FestivalPage.js
import React, { useState, useEffect } from "react";
import StarRating from "./StarRating";
import Favorite from "./Favorite";

// 長野県のお祭りリスト
const festivals = [
  { id: 1, name: "長野灯篭まつり" },
  { id: 2, name: "善光寺花まつり" },
  { id: 3, name: "戸隠そば祭り" },
  { id: 4, name: "松本ぼんぼん" },
  { id: 5, name: "諏訪湖祭湖上花火大会" }
];

export default function FestivalPage() {
  const [ratings, setRatings] = useState(() => {
    const saved = localStorage.getItem("festivalRatings");
    return saved ? JSON.parse(saved) : festivals.reduce((acc, f) => ({ ...acc, [f.id]: 0 }), {});
  });

  useEffect(() => {
    localStorage.setItem("festivalRatings", JSON.stringify(ratings));
  }, [ratings]);

  const handleRate = (id, rating) => {
    setRatings(prev => ({ ...prev, [id]: Number(rating) }));
  };

  const [favorites, setFavorites] = useState(() => {
    const saved = localStorage.getItem("festivalFavorites");
    return saved ? JSON.parse(saved) : festivals.reduce((acc, f) => ({ ...acc, [f.id]: false }), {});
  });

  const toggleFavorite = (id) => {
    setFavorites(prev => {
      const updated = { ...prev, [id]: !prev[id] };
      localStorage.setItem("festivalFavorites", JSON.stringify(updated));
      return updated;
    });
  };

  const [comments, setComments] = useState(() => {
    const saved = localStorage.getItem("festivalComments");
    return saved ? JSON.parse(saved) : festivals.reduce((acc, f) => ({ ...acc, [f.id]: [] }), {});
  });

  const [inputValues, setInputValues] = useState(() =>
    festivals.reduce((acc, f) => ({ ...acc, [f.id]: { name: "", text: "" } }), {})
  );

  const handleAddComment = (id) => {
    const { name, text } = inputValues[id];
    if (!text.trim()) return;

    const newComment = {
      name: name || "名無し",
      text: text.trim(),
      date: new Date().toLocaleString()
    };

    setComments(prev => {
      const updated = { ...prev, [id]: [...prev[id], newComment] };
      localStorage.setItem("festivalComments", JSON.stringify(updated));
      return updated;
    });

    setInputValues(prev => ({ ...prev, [id]: { name: "", text: "" } }));
  };

  const handleDeleteComment = (festivalId, index) => {
    setComments(prev => {
      const updated = { ...prev, [festivalId]: prev[festivalId].filter((_, i) => i !== index) };
      localStorage.setItem("festivalComments", JSON.stringify(updated));
      return updated;
    });
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "sans-serif" }}>
      <h1>長野県のお祭り</h1>
      {festivals.map(festival => (
        <div
          key={festival.id}
          style={{
            marginBottom: "2rem",
            padding: "1rem",
            border: "1px solid #ccc",
            borderRadius: "8px",
            maxWidth: "500px"
          }}
        >
          <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between" }}>
            <h2>{festival.name}</h2>
            <Favorite selected={favorites[festival.id]} onToggle={() => toggleFavorite(festival.id)} />
          </div>

          {/* 星評価 */}
          <StarRating
            count={5}
            value={ratings[festival.id] ?? 0}
            onRate={rate => handleRate(festival.id, rate)}
          />

          {/* コメントフォーム */}
          <div style={{ marginTop: "1rem" }}>
            <input
              type="text"
              placeholder="名前"
              value={inputValues[festival.id].name}
              onChange={e =>
                setInputValues(prev => ({
                  ...prev,
                  [festival.id]: { ...prev[festival.id], name: e.target.value }
                }))
              }
              style={{ width: "48%", padding: "0.5rem", marginRight: "4%" }}
            />
            <input
              type="text"
              placeholder="コメントを入力してEnter"
              value={inputValues[festival.id].text}
              onChange={e =>
                setInputValues(prev => ({
                  ...prev,
                  [festival.id]: { ...prev[festival.id], text: e.target.value }
                }))
              }
              onKeyDown={(e) => {
                if (e.key === "Enter") handleAddComment(festival.id);
              }}
              style={{ width: "48%", padding: "0.5rem" }}
            />

            <ul style={{ listStyle: "none", paddingLeft: 0, marginTop: "0.5rem" }}>
              {comments[festival.id]?.map((c, i) => (
                <li
                  key={i}
                  style={{
                    marginBottom: "0.3rem",
                    padding: "0.3rem",
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "center",
                    borderBottom: "1px solid #eee"
                  }}
                >
                  <div>
                    <strong>{c.name}</strong> ({c.date}): {c.text}
                  </div>
                  <button
                    onClick={() => handleDeleteComment(festival.id, i)}
                    style={{
                      background: "transparent",
                      border: "none",
                      color: "red",
                      cursor: "pointer"
                    }}
                  >
                    削除
                  </button>
                </li>
              ))}
            </ul>
          </div>
        </div>
      ))}
    </div>
  );
}
