import React, { useState, useEffect } from "react";
import StarRating from "./StarRating";

export default function FestivalPage() {
  const [festivals] = useState([
    { id: 1, name: "夏祭り", description: "屋台や花火が楽しめる" },
    { id: 2, name: "秋祭り", description: "紅葉と伝統舞踊が魅力" },
  ]);

  const [currentUser, setCurrentUser] = useState(null);
  const [userRatings, setUserRatings] = useState({});
  const [userFavorites, setUserFavorites] = useState([]);
  const [comments, setComments] = useState({});
  const [newComment, setNewComment] = useState("");

  // -----------------------------
  // 初期読み込み：ログインユーザー情報
  // -----------------------------
  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("loggedInUser"));
    setCurrentUser(user);

    if (user) {
      const storedRatings = JSON.parse(localStorage.getItem(`ratings_${user.username}`)) || {};
      const storedFavorites = JSON.parse(localStorage.getItem(`favorites_${user.username}`)) || [];
      setUserRatings(storedRatings);
      setUserFavorites(storedFavorites);
    }

    // コメントは共通
    const storedComments = JSON.parse(localStorage.getItem("comments")) || {};
    setComments(storedComments);
  }, []);

  // -----------------------------
  // 評価の保存（ユーザー別）
  // -----------------------------
  const handleRatingChange = (festivalId, rating) => {
    if (!currentUser) {
      alert("ログインが必要です。");
      return;
    }

    const updatedRatings = { ...userRatings, [festivalId]: rating };
    setUserRatings(updatedRatings);
    localStorage.setItem(`ratings_${currentUser.username}`, JSON.stringify(updatedRatings));
  };

  // -----------------------------
  // お気に入り登録／解除（ユーザー別）
  // -----------------------------
  const toggleFavorite = (festivalId) => {
    if (!currentUser) {
      alert("ログインが必要です。");
      return;
    }

    let updatedFavorites = [...userFavorites];
    if (updatedFavorites.includes(festivalId)) {
      updatedFavorites = updatedFavorites.filter((id) => id !== festivalId);
    } else {
      updatedFavorites.push(festivalId);
    }

    setUserFavorites(updatedFavorites);
    localStorage.setItem(`favorites_${currentUser.username}`, JSON.stringify(updatedFavorites));
  };

  // -----------------------------
  // コメント投稿（全員共有）
  // -----------------------------
  const addComment = (festivalId) => {
    if (!currentUser) {
      alert("ログインが必要です。");
      return;
    }

    if (!newComment.trim()) return;

    const timestamp = new Date().toLocaleString();
    const updatedComments = {
      ...comments,
      [festivalId]: [
        ...(comments[festivalId] || []),
        { user: currentUser.username, text: newComment, time: timestamp },
      ],
    };

    setComments(updatedComments);
    localStorage.setItem("comments", JSON.stringify(updatedComments));
    setNewComment("");
  };

  return (
    <div className="festival-page">
      <h1>お祭り一覧</h1>

      {festivals.map((festival) => (
        <div key={festival.id} className="festival-card">
          <h2>{festival.name}</h2>
          <p>{festival.description}</p>

          <StarRating
            rating={userRatings[festival.id] || 0}
            onRatingChange={(r) => handleRatingChange(festival.id, r)}
          />

          <button onClick={() => toggleFavorite(festival.id)}>
            {userFavorites.includes(festival.id) ? "★ お気に入り解除" : "☆ お気に入り登録"}
          </button>

          <div className="comments-section">
            <h3>コメント</h3>
            {(comments[festival.id] || []).map((c, idx) => (
              <p key={idx}>
                <strong>{c.user}</strong> ({c.time})：{c.text}
              </p>
            ))}

            {currentUser ? (
              <div>
                <input
                  type="text"
                  placeholder="コメントを入力"
                  value={newComment}
                  onChange={(e) => setNewComment(e.target.value)}
                />
                <button onClick={() => addComment(festival.id)}>投稿</button>
              </div>
            ) : (
              <p>コメントを投稿するにはログインしてください。</p>
            )}
          </div>
        </div>
      ))}
    </div>
  );
}
