// FestivalPage.js
import React, { useState, useEffect } from "react";
import { festivals } from "./FestivalData";
import StarRating from "./StarRating";
import Favorite from "./Favorite";

export default function FestivalPage() {
  // ログイン中ユーザー
  const storedUser = JSON.parse(localStorage.getItem("loggedInUser"));
  const currentUser = storedUser?.username;

  const [userData, setUserData] = useState({ favorites: [], ratings: {}, comments: {} });
  const [commentInputs, setCommentInputs] = useState({}); // 入力中コメント

  // 初回ロードでユーザーデータを取得
  useEffect(() => {
    if (!currentUser) return;
    const allData = JSON.parse(localStorage.getItem("userData")) || {};
    setUserData(allData[currentUser] || { favorites: [], ratings: {}, comments: {} });
  }, [currentUser]);

  // ユーザーデータ更新
  const updateUserData = (updateFunc) => {
    if (!currentUser) return;
    setUserData(prev => {
      const updated = updateFunc(prev);
      const allData = JSON.parse(localStorage.getItem("userData")) || {};
      allData[currentUser] = updated;
      localStorage.setItem("userData", JSON.stringify(allData));
      return updated;
    });
  };

  // 星評価
  const handleRate = (festivalId, rating) => {
    updateUserData(prev => ({
      ...prev,
      ratings: { ...prev.ratings, [festivalId]: rating }
    }));
  };

  // お気に入り切替
  const handleFavoriteToggle = (festivalId) => {
    updateUserData(prev => {
      const favorites = prev.favorites.includes(festivalId)
        ? prev.favorites.filter(id => id !== festivalId)
        : [...prev.favorites, festivalId];
      return { ...prev, favorites };
    });
  };

  // コメント投稿
  const handleCommentSubmit = (festivalId) => {
    const text = commentInputs[festivalId]?.trim();
    if (!text) return;

    const newComment = { text, date: new Date().toLocaleString() };
    updateUserData(prev => {
      const comments = prev.comments[festivalId] ? [...prev.comments[festivalId], newComment] : [newComment];
      return { ...prev, comments: { ...prev.comments, [festivalId]: comments } };
    });

    setCommentInputs(prev => ({ ...prev, [festivalId]: "" }));
  };

  if (!currentUser) return <p>ログインしてください</p>;

  return (
    <div style={{ padding: "2rem", fontFamily: "sans-serif" }}>
      <h1>長野県のお祭り</h1>
      {festivals.map(festival => (
        <div
          key={festival.id}
          style={{
            marginBottom: "1.5rem",
            padding: "1rem",
            border: "1px solid #ccc",
            borderRadius: "8px",
            maxWidth: "500px"
          }}
        >
          <h2 style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
            {festival.name}
            <Favorite
              selected={userData.favorites.includes(festival.id)}
              onToggle={() => handleFavoriteToggle(festival.id)}
            />
          </h2>

          <p>評価: {userData.ratings[festival.id] ?? 0}</p>
          <StarRating
            count={5}
            value={userData.ratings[festival.id] ?? 0}
            onRate={rate => handleRate(festival.id, rate)}
          />

          <div style={{ marginTop: "1rem" }}>
            <h4>コメント</h4>
            {userData.comments[festival.id]?.map((c, i) => (
              <p key={i}>[{c.date}] {c.text}</p>
            ))}

            <input
              type="text"
              placeholder="コメントを入力"
              value={commentInputs[festival.id] || ""}
              onChange={e =>
                setCommentInputs(prev => ({ ...prev, [festival.id]: e.target.value }))
              }
              style={{ width: "80%", marginRight: "0.5rem" }}
            />
            <button onClick={() => handleCommentSubmit(festival.id)}>投稿</button>
          </div>
        </div>
      ))}
    </div>
  );
}
