import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function AccountPage() {
  const [user, setUser] = useState(null);
  const [favorites, setFavorites] = useState([]);
  const [comments, setComments] = useState({});
  const navigate = useNavigate();

  // ✅ ログイン中のユーザーを取得
  useEffect(() => {
    const loggedInUser = JSON.parse(localStorage.getItem("loggedInUser"));
    if (!loggedInUser) {
      alert("ログインが必要です");
      navigate("/");
      return;
    }
    setUser(loggedInUser);

    // 個人データ読み込み
    const storedFavorites =
      JSON.parse(localStorage.getItem(`favorites_${loggedInUser.username}`)) ||
      [];
    const storedComments =
      JSON.parse(localStorage.getItem(`comments_${loggedInUser.username}`)) ||
      {};
    setFavorites(storedFavorites);
    setComments(storedComments);
  }, [navigate]);

  // ✅ ログアウト処理
  const handleLogout = () => {
    localStorage.removeItem("loggedInUser");
    alert("ログアウトしました");
    navigate("/");
  };

  // ✅ アカウント削除処理
  const handleDeleteAccount = () => {
    if (
      window.confirm(
        "本当にアカウントを削除しますか？この操作は取り消せません。"
      )
    ) {
      const username = user.username;

      // 関連データを削除
      localStorage.removeItem("loggedInUser");
      localStorage.removeItem("user"); // 登録済ユーザー情報
      localStorage.removeItem(`favorites_${username}`);
      localStorage.removeItem(`comments_${username}`);
      localStorage.removeItem(`ratings_${username}`);

      alert("アカウントを削除しました");
      navigate("/");
    }
  };

  if (!user) return null;

  return (
    <div style={{ padding: "2rem", fontFamily: "sans-serif" }}>
      <h1>マイページ</h1>
      <p>ログイン中のユーザー名：{user.username}</p>

      <hr style={{ margin: "1rem 0" }} />

      <h2>お気に入り一覧</h2>
      {favorites.length > 0 ? (
        favorites.map((f) => <p key={f.id}>⭐ {f.name}</p>)
      ) : (
        <p>お気に入り登録はありません。</p>
      )}

      <h2>コメント履歴</h2>
      {Object.keys(comments).length > 0 ? (
        Object.entries(comments).map(([festivalId, userComments]) => (
          <div key={festivalId}>
            <h4>お祭りID: {festivalId}</h4>
            {userComments.map((c, i) => (
              <p key={i}>
                {c.text}（{new Date(c.timestamp).toLocaleString()}）
              </p>
            ))}
          </div>
        ))
      ) : (
        <p>コメント履歴はありません。</p>
      )}

      <hr style={{ margin: "1.5rem 0" }} />

      <div style={{ display: "flex", gap: "1rem" }}>
        <button
          onClick={handleLogout}
          style={{
            background: "#007bff",
            color: "white",
            border: "none",
            padding: "10px 20px",
            borderRadius: "6px",
            cursor: "pointer",
          }}
        >
          ログアウト
        </button>

        <button
          onClick={handleDeleteAccount}
          style={{
            background: "#dc3545",
            color: "white",
            border: "none",
            padding: "10px 20px",
            borderRadius: "6px",
            cursor: "pointer",
          }}
        >
          アカウント削除
        </button>
      </div>
    </div>
  );
}
