// FestivalPage.js
import React, { useState } from "react";
import StarRating from "./StarRating";
import Favorite from "./Favorite";
import { Link } from "react-router-dom";

// お祭りリスト
const festivals = [
  { id: 1, name: "安曇野花火" },
  { id: 2, name: "上田わっしょい" },
  { id: 3, name: "信州上田大花火大会" },
  { id: 4, name: "夏まつり松本ぼんぼん" },
  { id: 5, name: "真田まつり" }
];

export default function FestivalPage() {
  // 星評価
  const [ratings, setRatings] = useState(() => {
    const saved = localStorage.getItem("festivalRatings");
    return saved ? JSON.parse(saved) : festivals.reduce((acc, f) => ({ ...acc, [f.id]: 0 }), {});
  });

  // お気に入り
  const [favorites, setFavorites] = useState(() => {
    const saved = localStorage.getItem("festivalFavorites");
    return saved ? JSON.parse(saved) : festivals.reduce((acc, f) => ({ ...acc, [f.id]: false }), {});
  });

  // コメント
  const [comments, setComments] = useState(() => {
    const saved = localStorage.getItem("festivalComments");
    return saved ? JSON.parse(saved) : festivals.reduce((acc, f) => ({ ...acc, [f.id]: [] }), {});
  });

  // コメント・名前入力まとめ
  const [inputs, setInputs] = useState(() =>
    festivals.reduce((acc, f) => ({ ...acc, [f.id]: { name: "", comment: "" } }), {})
  );

  // 星評価更新
  const handleRate = (id, rating) => {
    setRatings(prev => {
      const updated = { ...prev, [id]: Number(rating) };
      localStorage.setItem("festivalRatings", JSON.stringify(updated));
      return updated;
    });
  };

  // お気に入り切替
  const toggleFavorite = (id) => {
    setFavorites(prev => {
      const updated = { ...prev, [id]: !prev[id] };
      localStorage.setItem("festivalFavorites", JSON.stringify(updated));
      return updated;
    });
  };

  // コメント追加
  const handleAddComment = (id) => {
    const input = inputs[id];
    if (!input.comment.trim()) return;

    const newComment = {
      name: input.name.trim() || "名無し",
      text: input.comment.trim(),
      date: new Date().toLocaleString()
    };

    setComments(prev => {
      const updated = { ...prev, [id]: [...prev[id], newComment] };
      localStorage.setItem("festivalComments", JSON.stringify(updated));
      return updated;
    });

    setInputs(prev => ({ ...prev, [id]: { name: input.name, comment: "" } }));
  };

  // コメント削除
  const handleDeleteComment = (festivalId, index) => {
    setComments(prev => {
      const updated = { ...prev, [festivalId]: prev[festivalId].filter((_, i) => i !== index) };
      localStorage.setItem("festivalComments", JSON.stringify(updated));
      return updated;
    });
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "sans-serif" }}>
      <h1>長野県のお祭り</h1>

      <div style={{ marginBottom: "1rem" }}>
        <Link to="/account">アカウントページへ</Link>
      </div>

      {festivals.map(festival => (
        <div
          key={festival.id}
          style={{
            marginBottom: "2rem",
            padding: "1rem",
            border: "1px solid #ccc",
            borderRadius: "8px",
            maxWidth: "500px"
          }}
        >
          <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between" }}>
            <h2>{festival.name}</h2>
            <Favorite selected={favorites[festival.id]} onToggle={() => toggleFavorite(festival.id)} />
          </div>

          <StarRating
            count={5}
            value={ratings[festival.id]}
            onRate={rate => handleRate(festival.id, rate)}
          />

          <div style={{ marginTop: "1rem" }}>
            <input
              type="text"
              placeholder="名前"
              value={inputs[festival.id].name}
              onChange={e => setInputs(prev => ({ ...prev, [festival.id]: { ...prev[festival.id], name: e.target.value } }))}
              style={{ width: "48%", padding: "0.5rem", marginRight: "4%" }}
            />
            <input
              type="text"
              placeholder="コメントを追加（Enterで送信）"
              value={inputs[festival.id].comment}
              onChange={e => setInputs(prev => ({ ...prev, [festival.id]: { ...prev[festival.id], comment: e.target.value } }))}
              onKeyDown={e => { if (e.key === "Enter") handleAddComment(festival.id); }}
              style={{ width: "48%", padding: "0.5rem" }}
            />
            <ul style={{ listStyle: "none", paddingLeft: 0, marginTop: "0.5rem" }}>
              {comments[festival.id].map((c, i) => (
                <li key={i} style={{ display: "flex", justifyContent: "space-between", alignItems: "center", borderBottom: "1px solid #eee", padding: "0.3rem 0" }}>
                  <span><strong>{c.name}</strong> ({c.date}): {c.text}</span>
                  <button onClick={() => handleDeleteComment(festival.id, i)} style={{ color: "red", background: "transparent", border: "none", cursor: "pointer" }}>
                    削除
                  </button>
                </li>
              ))}
            </ul>
          </div>
        </div>
      ))}
    </div>
  );
}
