import React, { useState, useEffect, useContext } from "react";
import { festivals } from "./FestivalData";
import StarRating from "./StarRating";
import Favorite from "./Favorite";
import { UserContext } from "./App";

const safeParse = (key, fallback = {}) => {
  try {
    const item = localStorage.getItem(key);
    return item ? JSON.parse(item) : fallback;
  } catch {
    return fallback;
  }
};

export default function FestivalPage() {
  const { user } = useContext(UserContext);
  const username = user?.username || null;

  const [ratings, setRatings] = useState({});
  const [favorites, setFavorites] = useState({});
  const [comments, setComments] = useState({});
  const [commentInputs, setCommentInputs] = useState({});
  const [editId, setEditId] = useState(null);
  const [editText, setEditText] = useState("");

  // 初期ロード
  useEffect(() => {
    if (!username) return;

    // 自分のデータ
    setRatings(safeParse(`festivalRatings_${username}`));
    setFavorites(safeParse(`festivalFavorites_${username}`));

    // コメントは共通
    setComments(safeParse("festivalComments", {}));
  }, [username]);

  // 保存
  useEffect(() => {
    if (username)
      localStorage.setItem(`festivalRatings_${username}`, JSON.stringify(ratings));
  }, [ratings, username]);

  useEffect(() => {
    if (username)
      localStorage.setItem(`festivalFavorites_${username}`, JSON.stringify(favorites));
  }, [favorites, username]);

  useEffect(() => {
    localStorage.setItem("festivalComments", JSON.stringify(comments));
  }, [comments]);

  // ログインしていない場合
  if (!username)
    return (
      <div style={{ padding: "2rem" }}>
        <h2>ログインが必要です。</h2>
        <p>
          <a href="/" style={{ color: "#007bff" }}>
            ログインページへ
          </a>{" "}
          または{" "}
          <a href="/register" style={{ color: "#007bff" }}>
            新規登録
          </a>{" "}
          に進んでください。
        </p>
      </div>
    );

  // 評価
  const handleRate = (id, value) => {
    const updated = { ...ratings, [id]: Number(value) };
    setRatings(updated);
  };

  // お気に入り
  const toggleFavorite = (id) => {
    const updated = { ...favorites, [id]: !favorites[id] };
    setFavorites(updated);
  };

  // コメント追加
  const handleAddComment = (id, text) => {
    if (!text.trim()) return;
    const newComment = {
      user: username,
      text,
      timestamp: Date.now(),
      date: new Date().toLocaleString(),
    };
    const updated = {
      ...comments,
      [id]: [...(comments[id] || []), newComment],
    };
    setComments(updated);
    setCommentInputs((prev) => ({ ...prev, [id]: "" }));
  };

  // コメント削除
  const handleDeleteComment = (id, timestamp) => {
    const updated = {
      ...comments,
      [id]: comments[id].filter((c) => c.timestamp !== timestamp),
    };
    setComments(updated);
  };

  // 編集開始
  const handleEditComment = (id, timestamp) => {
    setEditId(`${id}-${timestamp}`);
    const target = comments[id].find((c) => c.timestamp === timestamp);
    setEditText(target.text);
  };

  // 編集保存
  const handleSaveEdit = (id, timestamp) => {
    if (!editText.trim()) return;
    const updated = {
      ...comments,
      [id]: comments[id].map((c) =>
        c.timestamp === timestamp
          ? { ...c, text: editText, date: new Date().toLocaleString() }
          : c
      ),
    };
    setComments(updated);
    setEditId(null);
    setEditText("");
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "sans-serif" }}>
      <h1>長野県のお祭り</h1>

      {festivals.map((f) => (
        <div
          key={f.id}
          style={{
            marginBottom: "2rem",
            padding: "1rem",
            border: "1px solid #ccc",
            borderRadius: "8px",
            maxWidth: "500px",
          }}
        >
          {/* タイトル＆お気に入り */}
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
            }}
          >
            <h2>{f.name}</h2>
            <Favorite
              selected={favorites[f.id]}
              onToggle={() => toggleFavorite(f.id)}
            />
            {favorites[f.id] && (
              <button
                onClick={() => toggleFavorite(f.id)}
                style={{
                  marginLeft: "0.5rem",
                  color: "red",
                  cursor: "pointer",
                  border: "none",
                  background: "transparent",
                }}
              >
                お気に入り解除
              </button>
            )}
          </div>

          {/* 評価 */}
          <StarRating
            count={5}
            value={ratings[f.id] || 0}
            onRate={(rate) => handleRate(f.id, rate)}
          />

          {/* コメント */}
          <div style={{ marginTop: "1rem" }}>
            <input
              type="text"
              placeholder="コメントを追加"
              value={commentInputs[f.id] || ""}
              onChange={(e) =>
                setCommentInputs((prev) => ({ ...prev, [f.id]: e.target.value }))
              }
              onKeyDown={(e) => {
                if (e.key === "Enter")
                  handleAddComment(f.id, commentInputs[f.id] || "");
              }}
              style={{ width: "100%", padding: "0.5rem" }}
            />
            <ul style={{ listStyle: "none", padding: 0, marginTop: "0.5rem" }}>
              {(comments[f.id] || []).map((c) => {
                const isEditing = editId === `${f.id}-${c.timestamp}`;
                return (
                  <li
                    key={c.timestamp}
                    style={{
                      display: "flex",
                      justifyContent: "space-between",
                      borderBottom: "1px solid #eee",
                      padding: "0.3rem 0",
                    }}
                  >
                    <div>
                      <strong>{c.user}</strong>（{c.date}）:
                      {isEditing ? (
                        <input
                          value={editText}
                          onChange={(e) => setEditText(e.target.value)}
                        />
                      ) : (
                        <> {c.text}</>
                      )}
                    </div>
                    {c.user === username && (
                      <div>
                        {isEditing ? (
                          <button onClick={() => handleSaveEdit(f.id, c.timestamp)}>
                            保存
                          </button>
                        ) : (
                          <>
                            <button
                              onClick={() => handleEditComment(f.id, c.timestamp)}
                            >
                              編集
                            </button>
                            <button
                              onClick={() => handleDeleteComment(f.id, c.timestamp)}
                              style={{ marginLeft: "0.3rem", color: "red" }}
                            >
                              削除
                            </button>
                          </>
                        )}
                      </div>
                    )}
                  </li>
                );
              })}
            </ul>
          </div>
        </div>
      ))}
    </div>
  );
}
