// FestivalData.js
export const festivals = [
  { id: 1, name: "安曇野花火" },
  { id: 2, name: "上田わっしょい" },
  { id: 3, name: "信州上田大花火大会" },
  { id: 4, name: "夏まつり松本ぼんぼん" },
  { id: 5, name: "真田まつり" }
];

// before（全員共通）
localStorage.setItem("favorites", JSON.stringify(favorites));

// after（ユーザーごと）
localStorage.setItem(`favorites_${user.username}`, JSON.stringify(favorites));
