import React, { useContext } from "react";
import { UserContext } from "./App";
import { festivals } from "./FestivalData";

export default function AccountPage() {
  const { user } = useContext(UserContext);
  if (!user) return <p>ログインしてください</p>;

  const ratings = JSON.parse(localStorage.getItem("festivalRatings") || "{}");
  const favorites = JSON.parse(localStorage.getItem("festivalFavorites") || "{}");
  const comments = JSON.parse(localStorage.getItem("festivalComments") || "{}");

  return (
    <div style={{ padding: "2rem", fontFamily: "sans-serif" }}>
      <h1>{user.username} さんのアカウント</h1>

      <h2>お気に入りのお祭り</h2>
      <ul>
        {festivals.filter(f => favorites[f.id]).map(f => <li key={f.id}>{f.name}</li>)}
      </ul>

      <h2>評価したお祭り</h2>
      <ul>
        {festivals.filter(f => ratings[f.id] > 0).map(f => <li key={f.id}>{f.name}: {ratings[f.id]} / 5</li>)}
      </ul>

      <h2>コメント</h2>
      <ul>
        {festivals.flatMap(f => (comments[f.id] || []).filter(c => c.name === user.username)
          .map((c, i) => <li key={`${f.id}-${i}`}><strong>{f.name}</strong>: {c.text} ({c.date})</li>))}
      </ul>
    </div>
  );
}
