import React, { useState, useEffect } from "react";
import StarRating from "./StarRating";
import Favorite from "./Favorite";
import { festivals } from "./FestivalData";

export default function AccountPage() {
  const [user, setUser] = useState(() => {
    const savedUser = localStorage.getItem("loggedInUser");
    return savedUser ? JSON.parse(savedUser) : null;
  });

  const [ratings, setRatings] = useState(() => JSON.parse(localStorage.getItem("festivalRatings") || "{}"));
  const [favorites, setFavorites] = useState(() => JSON.parse(localStorage.getItem("festivalFavorites") || "{}"));
  const [comments, setComments] = useState(() => JSON.parse(localStorage.getItem("festivalComments") || "{}"));
  const [editId, setEditId] = useState(null);
  const [editText, setEditText] = useState("");

  useEffect(() => localStorage.setItem("festivalRatings", JSON.stringify(ratings)), [ratings]);
  useEffect(() => localStorage.setItem("festivalFavorites", JSON.stringify(favorites)), [favorites]);
  useEffect(() => localStorage.setItem("festivalComments", JSON.stringify(comments)), [comments]);

  if (!user) return <p>ログインしてください</p>;

  const handleRate = (id, rating) => setRatings(prev => ({ ...prev, [id]: Number(rating) }));
  const removeFavorite = (id) => setFavorites(prev => ({ ...prev, [id]: false }));
  const handleDeleteComment = (id, index) => setComments(prev => ({ ...prev, [id]: prev[id].filter((_, i) => i !== index) }));
  const handleEditComment = (id, index) => {
    setEditId(`${id}-${index}`);
    setEditText(comments[id][index].text);
  };
  const handleSaveEdit = (id, index) => {
    if (!editText.trim()) return;
    setComments(prev => {
      const updated = [...prev[id]];
      updated[index] = { ...updated[index], text: editText, date: new Date().toLocaleString() };
      return { ...prev, [id]: updated };
    });
    setEditId(null);
    setEditText("");
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "sans-serif" }}>
      <h1>{user.username} さんのアカウント</h1>

      <section>
        <h2>お気に入りのお祭り</h2>
        {festivals.filter(f => favorites[f.id]).length === 0 && <p>お気に入りはありません</p>}
        {festivals.map(f => favorites[f.id] && (
          <div key={f.id} style={{ marginBottom: "1rem" }}>
            <span>{f.name}</span>
            <Favorite selected={true} onToggle={() => removeFavorite(f.id)} />
            <button onClick={() => removeFavorite(f.id)} style={{ marginLeft: "0.5rem", color: "red" }}>解除</button>
          </div>
        ))}
      </section>

      <section style={{ marginTop: "2rem" }}>
        <h2>評価したお祭り</h2>
        {festivals.filter(f => ratings[f.id] > 0).length === 0 && <p>まだ評価していません</p>}
        {festivals.map(f => ratings[f.id] > 0 && (
          <div key={f.id} style={{ marginBottom: "1rem" }}>
            <span>{f.name}</span>
            <StarRating count={5} value={ratings[f.id]} onRate={rate => handleRate(f.id, rate)} />
          </div>
        ))}
      </section>

      <section style={{ marginTop: "2rem" }}>
        <h2>コメントしたお祭り</h2>
        {festivals.filter(f => comments[f.id]?.length > 0).length === 0 && <p>まだコメントしていません</p>}
        {festivals.map(f => comments[f.id]?.length > 0 && (
          <div key={f.id} style={{ marginBottom: "1rem" }}>
            <strong>{f.name}</strong>
            <ul style={{ listStyle: "none", paddingLeft: 0 }}>
              {comments[f.id].map((c, i) => {
                const isEditing = editId === `${f.id}-${i}`;
                return (
                  <li key={i} style={{ display: "flex", justifyContent: "space-between" }}>
                    <span>
                      <strong>{c.name}</strong> ({c.date}):{" "}
                      {isEditing ? (
                        <input value={editText} onChange={e => setEditText(e.target.value)} />
                      ) : c.text}
                    </span>
                    {c.name === user.username && (
                      <div>
                        {isEditing ? (
                          <button onClick={() => handleSaveEdit(f.id, i)}>保存</button>
                        ) : (
                          <>
                            <button onClick={() => handleEditComment(f.id, i)}>編集</button>
                            <button onClick={() => handleDeleteComment(f.id, i)} style={{ marginLeft: "0.3rem", color: "red" }}>削除</button>
                          </>
                        )}
                      </div>
                    )}
                  </li>
                );
              })}
            </ul>
          </div>
        ))}
      </section>
    </div>
  );
}
