// FestivalPage.js
import React, { useState } from "react";
import StarRating from "./StarRating";

// 仮のフェス情報
const festivals = [
  { id: 1, name: "Summer Fest" },
  { id: 2, name: "Music Fiesta" },
  { id: 3, name: "Food Carnival" }
];

export default function FestivalPage() {
  // 初期値を全て 0 に設定
  const [ratings, setRatings] = useState(
    festivals.reduce((acc, f) => ({ ...acc, [f.id]: 0 }), {})
  );

  const handleRate = (id, rating) => {
    console.log("festivalId:", id, "rating:", rating, typeof rating);
    setRatings(prev => ({ ...prev, [id]: rating }));
  };

  return (
    <div>
      <h1>Festivals</h1>
      {festivals.map(festival => (
        <div key={festival.id} style={{ marginBottom: "1rem" }}>
          <h2>{festival.name}</h2>
          {/* フォールバックもあるとさらに安全 */}
          <p>Rating: {ratings[festival.id] ?? 0}</p>
          <StarRating
            count={5}
            value={ratings[festival.id] ?? 0}
            onRate={(rate) => handleRate(festival.id, rate)}
          />
        </div>
      ))}
    </div>
  );
}
