// FestivalPage.js
import React, { useState, useEffect } from "react";
import StarRating from "./StarRating";
import Favorite from "./Favorite";
import { Link } from "react-router-dom";

// 長野県のお祭りリスト
const festivals = [
  { id: 1, name: "安曇野花火" },
  { id: 2, name: "上田わっしょい" },
  { id: 3, name: "信州上田大花火大会" },
  { id: 4, name: "夏まつり松本ぼんぼん" },
  { id: 5, name: "真田まつり" }
];

export default function FestivalPage() {
  // 星評価
  const [ratings, setRatings] = useState(() => {
    const saved = localStorage.getItem("festivalRatings");
    return saved ? JSON.parse(saved) : festivals.reduce((acc, f) => ({ ...acc, [f.id]: 0 }), {});
  });

  // お気に入り
  const [favorites, setFavorites] = useState(() => {
    const saved = localStorage.getItem("festivalFavorites");
    return saved ? JSON.parse(saved) : festivals.reduce((acc, f) => ({ ...acc, [f.id]: false }), {});
  });

  // コメント
  const [comments, setComments] = useState(() => {
    const saved = localStorage.getItem("festivalComments");
    return saved ? JSON.parse(saved) : festivals.reduce((acc, f) => ({ ...acc, [f.id]: [] }), {});
  });

  // コメント入力用 state
  const [nameInputs, setNameInputs] = useState({});
  const [commentInputs, setCommentInputs] = useState({});

  // localStorage 更新
  useEffect(() => {
    localStorage.setItem("festivalRatings", JSON.stringify(ratings));
    localStorage.setItem("festivalFavorites", JSON.stringify(favorites));
    localStorage.setItem("festivalComments", JSON.stringify(comments));
  }, [ratings, favorites, comments]);

  // 星評価更新
  const handleRate = (id, rating) => {
    setRatings(prev => ({ ...prev, [id]: Number(rating) }));
  };

  // お気に入り切り替え
  const toggleFavorite = (id) => {
    setFavorites(prev => ({ ...prev, [id]: !prev[id] }));
  };

  // コメント追加
  const handleAddComment = (id) => {
    if (!commentInputs[id]?.trim()) return;

    const newComment = {
      name: nameInputs[id] || "名無し",
      text: commentInputs[id],
      date: new Date().toLocaleString()
    };

    setComments(prev => ({
      ...prev,
      [id]: [...prev[id], newComment]
    }));

    setCommentInputs(prev => ({ ...prev, [id]: "" }));
  };

  // コメント削除
  const handleDeleteComment = (festivalId, index) => {
    setComments(prev => ({
      ...prev,
      [festivalId]: prev[festivalId].filter((_, i) => i !== index)
    }));
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "sans-serif" }}>
      <h1>長野県のお祭り</h1>

      {/* アカウントページへのリンク */}
      <div style={{ marginBottom: "1rem" }}>
        <Link to="/account">アカウントページへ</Link>
      </div>

      {festivals.map(festival => (
        <div
          key={festival.id}
          style={{
            marginBottom: "2rem",
            padding: "1rem",
            border: "1px solid #ccc",
            borderRadius: "8px",
            maxWidth: "500px"
          }}
        >
          <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between" }}>
            <h2>{festival.name}</h2>
            <Favorite selected={favorites[festival.id]} onToggle={() => toggleFavorite(festival.id)} />
          </div>

          {/* 星評価 */}
          <StarRating
            count={5}
            value={ratings[festival.id] ?? 0}
            onRate={rate => handleRate(festival.id, rate)}
          />

          {/* コメント */}
          <div style={{ marginTop: "1rem" }}>
            <input
              type="text"
              placeholder="名前"
              value={nameInputs[festival.id] || ""}
              onChange={e => setNameInputs(prev => ({ ...prev, [festival.id]: e.target.value }))}
              style={{ width: "48%", padding: "0.5rem", marginRight: "4%" }}
            />
            <input
              type="text"
              placeholder="コメントを追加（Enterで送信）"
              value={commentInputs[festival.id] || ""}
              onChange={e => setCommentInputs(prev => ({ ...prev, [festival.id]: e.target.value }))}
              onKeyDown={e => {
                if (e.key === "Enter") handleAddComment(festival.id);
              }}
              style={{ width: "48%", padding: "0.5rem" }}
            />
            <ul style={{ listStyle: "none", paddingLeft: 0, marginTop: "0.5rem" }}>
              {comments[festival.id]?.map((c, i) => (
                <li
                  key={i}
                  style={{
                    marginBottom: "0.3rem",
                    padding: "0.3rem",
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "center",
                    borderBottom: "1px solid #eee"
                  }}
                >
                  <div>
                    <strong>{c.name}</strong> ({c.date}): {c.text}
                  </div>
                  <button
                    onClick={() => handleDeleteComment(festival.id, i)}
                    style={{
                      background: "transparent",
                      border: "none",
                      color: "red",
                      cursor: "pointer"
                    }}
                  >
                    削除
                  </button>
                </li>
              ))}
            </ul>
          </div>
        </div>
      ))}
    </div>
  );
}
