import React, { useState, useEffect, useContext } from "react";
import { festivals } from "./FestivalData";
import StarRating from "./StarRating";
import Favorite from "./Favorite";
import { UserContext } from "./App";

// ÂÆâÂÖ®„Å´JSON„Éë„Éº„Çπ
const safeParse = (key, fallback = {}) => {
  try {
    const item = localStorage.getItem(key);
    return item ? JSON.parse(item) : fallback;
  } catch {
    return fallback;
  }
};

export default function FestivalPage() {
  const { user } = useContext(UserContext);
  const username = user?.username || null;

  const [ratings, setRatings] = useState({});
  const [favorites, setFavorites] = useState({});
  const [comments, setComments] = useState({});
  const [commentInputs, setCommentInputs] = useState({});
  const [editId, setEditId] = useState(null);
  const [editText, setEditText] = useState("");

  // üîπ ÂàùÊúü„É≠„Éº„Éâ
  useEffect(() => {
    if (!username) return;

    const allData = safeParse("festivalData", {});
    const userData = allData[username] || { ratings: {}, favorites: {}, comments: {} };

    setRatings(userData.ratings);
    setFavorites(userData.favorites);
    setComments(userData.comments);
  }, [username]);

  // üîπ „Éá„Éº„Çø‰øùÂ≠ò„Éò„É´„Éë„Éº
  const saveData = (newRatings, newFavorites, newComments) => {
    if (!username) return;
    const allData = safeParse("festivalData", {});
    allData[username] = {
      ratings: newRatings,
      favorites: newFavorites,
      comments: newComments,
    };
    localStorage.setItem("festivalData", JSON.stringify(allData));
  };

  // ‚≠ê Ë©ï‰æ°
  const handleRate = (id, value) => {
    const updated = { ...ratings, [id]: Number(value) };
    setRatings(updated);
    saveData(updated, favorites, comments);
  };

  // üíñ „ÅäÊ∞ó„Å´ÂÖ•„Çä
  const toggleFavorite = (id) => {
    const updated = { ...favorites, [id]: !favorites[id] };
    setFavorites(updated);
    saveData(ratings, updated, comments);
  };

  // üí¨ „Ç≥„É°„É≥„ÉàËøΩÂä†„ÉªÂÖ•Âäõ‰∏≠„Åß„ÇÇ‰øùÂ≠ò
  const handleCommentInput = (id, value) => {
    setCommentInputs((prev) => ({ ...prev, [id]: value }));

    const updatedComments = {
      ...comments,
      [id]: comments[id] || [],
    };
    saveData(ratings, favorites, updatedComments);
  };

  const handleAddComment = (id) => {
    const text = commentInputs[id];
    if (!text?.trim()) return;
    const newComment = {
      user: username,
      text,
      timestamp: Date.now(),
      date: new Date().toLocaleString(),
    };
    const updated = {
      ...comments,
      [id]: [...(comments[id] || []), newComment],
    };
    setComments(updated);
    setCommentInputs((prev) => ({ ...prev, [id]: "" }));
    saveData(ratings, favorites, updated);
  };

  // üí¨ „Ç≥„É°„É≥„ÉàÂâäÈô§
  const handleDeleteComment = (id, timestamp) => {
    const updated = {
      ...comments,
      [id]: comments[id].filter((c) => c.timestamp !== timestamp),
    };
    setComments(updated);
    saveData(ratings, favorites, updated);
  };

  // üí¨ „Ç≥„É°„É≥„ÉàÁ∑®ÈõÜ
  const handleEditComment = (id, timestamp) => {
    setEditId(`${id}-${timestamp}`);
    const target = comments[id].find((c) => c.timestamp === timestamp);
    setEditText(target.text);
  };

  const handleSaveEdit = (id, timestamp) => {
    if (!editText.trim()) return;
    const updated = {
      ...comments,
      [id]: comments[id].map((c) =>
        c.timestamp === timestamp
          ? { ...c, text: editText, date: new Date().toLocaleString() }
          : c
      ),
    };
    setComments(updated);
    setEditId(null);
    setEditText("");
    saveData(ratings, favorites, updated);
  };

  if (!username)
    return (
      <div style={{ padding: "2rem" }}>
        <h2>„É≠„Ç∞„Ç§„É≥„ÅåÂøÖË¶Å„Åß„Åô„ÄÇ</h2>
        <p>
          <a href="/" style={{ color: "#007bff" }}>
            „É≠„Ç∞„Ç§„É≥„Éö„Éº„Ç∏„Å∏
          </a>{" "}
          „Åæ„Åü„ÅØ{" "}
          <a href="/register" style={{ color: "#007bff" }}>
            Êñ∞Ë¶èÁôªÈå≤
          </a>{" "}
          „Å´ÈÄ≤„Çì„Åß„Åè„Å†„Åï„ÅÑ„ÄÇ
        </p>
      </div>
    );

  return (
    <div style={{ padding: "2rem", fontFamily: "sans-serif" }}>
      <h1>Èï∑ÈáéÁúå„ÅÆ„ÅäÁ•≠„Çä</h1>

      {festivals.map((f) => (
        <div
          key={f.id}
          style={{
            marginBottom: "2rem",
            padding: "1rem",
            border: "1px solid #ccc",
            borderRadius: "8px",
            maxWidth: "500px",
          }}
        >
          {/* „Çø„Ç§„Éà„É´ÔºÜ„ÅäÊ∞ó„Å´ÂÖ•„Çä */}
          <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
            <h2>{f.name}</h2>
            <Favorite selected={favorites[f.id]} onToggle={() => toggleFavorite(f.id)} />
            {favorites[f.id] && (
              <button
                onClick={() => toggleFavorite(f.id)}
                style={{
                  marginLeft: "0.5rem",
                  color: "red",
                  cursor: "pointer",
                  border: "none",
                  background: "transparent",
                }}
              >
                „ÅäÊ∞ó„Å´ÂÖ•„ÇäËß£Èô§
              </button>
            )}
          </div>

          {/* Ë©ï‰æ° */}
          <StarRating count={5} value={ratings[f.id] || 0} onRate={(rate) => handleRate(f.id, rate)} />

          {/* „Ç≥„É°„É≥„Éà */}
          <div style={{ marginTop: "1rem" }}>
            <input
              type="text"
              placeholder="„Ç≥„É°„É≥„Éà„ÇíËøΩÂä†"
              value={commentInputs[f.id] || ""}
              onChange={(e) => handleCommentInput(f.id, e.target.value)}
              onKeyDown={(e) => {
                if (e.key === "Enter") handleAddComment(f.id);
              }}
              style={{ width: "100%", padding: "0.5rem" }}
            />
            <ul style={{ listStyle: "none", padding: 0, marginTop: "0.5rem" }}>
              {(comments[f.id] || []).map((c) => {
                const isEditing = editId === `${f.id}-${c.timestamp}`;
                return (
                  <li key={c.timestamp} style={{ display: "flex", justifyContent: "space-between", borderBottom: "1px solid #eee", padding: "0.3rem 0" }}>
                    <div>
                      <strong>{c.user}</strong>Ôºà{c.date}Ôºâ:
                      {isEditing ? (
                        <input value={editText} onChange={(e) => setEditText(e.target.value)} />
                      ) : (
                        <> {c.text}</>
                      )}
                    </div>
                    {c.user === username && (
                      <div>
                        {isEditing ? (
                          <button onClick={() => handleSaveEdit(f.id, c.timestamp)}>‰øùÂ≠ò</button>
                        ) : (
                          <>
                            <button onClick={() => handleEditComment(f.id, c.timestamp)}>Á∑®ÈõÜ</button>
                            <button onClick={() => handleDeleteComment(f.id, c.timestamp)} style={{ marginLeft: "0.3rem", color: "red" }}>
                              ÂâäÈô§
                            </button>
                          </>
                        )}
                      </div>
                    )}
                  </li>
                );
              })}
            </ul>
          </div>
        </div>
      ))}
    </div>
  );
}
