import React, { useState, useEffect, useContext } from "react";
import { festivals } from "./FestivalData";
import StarRating from "./StarRating";
import Favorite from "./Favorite";
import { UserContext } from "./App";

const safeParse = (key) => {
  try {
    const item = localStorage.getItem(key);
    return item ? JSON.parse(item) : {};
  } catch {
    return {};
  }
};

export default function FestivalPage() {
  const { user } = useContext(UserContext);
  const username = user?.username || null;

  const [ratings, setRatings] = useState({});
  const [favorites, setFavorites] = useState({});
  const [comments, setComments] = useState({});
  const [commentInputs, setCommentInputs] = useState({});
  const [editId, setEditId] = useState(null);
  const [editText, setEditText] = useState("");

  // ‚úÖ „É≠„Ç∞„Ç§„É≥Áä∂ÊÖãÁ¢∫Ë™ç
  useEffect(() => {
    if (!username) return;

    setRatings(safeParse(`festivalRatings_${username}`));
    setFavorites(safeParse(`festivalFavorites_${username}`));
    setComments(safeParse(`festivalComments_${username}`));
  }, [username]);

  // ‚úÖ ‰øùÂ≠ò
  useEffect(() => {
    if (username) localStorage.setItem(`festivalRatings_${username}`, JSON.stringify(ratings));
  }, [ratings, username]);
  useEffect(() => {
    if (username) localStorage.setItem(`festivalFavorites_${username}`, JSON.stringify(favorites));
  }, [favorites, username]);
  useEffect(() => {
    if (username) localStorage.setItem(`festivalComments_${username}`, JSON.stringify(comments));
  }, [comments, username]);

  if (!username)
    return (
      <div style={{ padding: "2rem" }}>
        <h2>„É≠„Ç∞„Ç§„É≥„ÅåÂøÖË¶Å„Åß„Åô„ÄÇ</h2>
        <p>
          <a href="/" style={{ color: "#007bff" }}>
            „É≠„Ç∞„Ç§„É≥„Éö„Éº„Ç∏„Å∏
          </a>{" "}
          „Åæ„Åü„ÅØ{" "}
          <a href="/register" style={{ color: "#007bff" }}>
            Êñ∞Ë¶èÁôªÈå≤
          </a>{" "}
          „Å´ÈÄ≤„Çì„Åß„Åè„Å†„Åï„ÅÑ„ÄÇ
        </p>
      </div>
    );

  // ‚≠ê Ë©ï‰æ°Ê©üËÉΩ
  const handleRate = (id, value) => setRatings((prev) => ({ ...prev, [id]: Number(value) }));

  // ‚ù§Ô∏è „ÅäÊ∞ó„Å´ÂÖ•„ÇäÂàá„ÇäÊõø„Åà
  const toggleFavorite = (id) =>
    setFavorites((prev) => ({ ...prev, [id]: !prev[id] }));

  // üí¨ „Ç≥„É°„É≥„ÉàËøΩÂä†
  const handleAddComment = (id, text) => {
    if (!text.trim()) return;
    const newComment = {
      user: username,
      text,
      timestamp: Date.now(),
      date: new Date().toLocaleString(),
    };
    setComments((prev) => ({
      ...prev,
      [id]: [...(prev[id] || []), newComment],
    }));
    setCommentInputs((prev) => ({ ...prev, [id]: "" }));
  };

  // üí¨ „Ç≥„É°„É≥„ÉàÂâäÈô§
  const handleDeleteComment = (id, timestamp) => {
    setComments((prev) => ({
      ...prev,
      [id]: prev[id].filter((c) => c.timestamp !== timestamp),
    }));
  };

  // ‚úèÔ∏è „Ç≥„É°„É≥„ÉàÁ∑®ÈõÜ
  const handleEditComment = (id, timestamp) => {
    setEditId(`${id}-${timestamp}`);
    const target = comments[id].find((c) => c.timestamp === timestamp);
    setEditText(target.text);
  };

  const handleSaveEdit = (id, timestamp) => {
    if (!editText.trim()) return;
    setComments((prev) => {
      const updated = prev[id].map((c) =>
        c.timestamp === timestamp
          ? { ...c, text: editText, date: new Date().toLocaleString() }
          : c
      );
      return { ...prev, [id]: updated };
    });
    setEditId(null);
    setEditText("");
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "sans-serif" }}>
      <h1>Èï∑ÈáéÁúå„ÅÆ„ÅäÁ•≠„Çä</h1>
      {festivals.map((f) => (
        <div
          key={f.id}
          style={{
            marginBottom: "2rem",
            padding: "1rem",
            border: "1px solid #ccc",
            borderRadius: "8px",
            maxWidth: "500px",
          }}
        >
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
            }}
          >
            <h2>{f.name}</h2>
            <Favorite
              selected={favorites[f.id]}
              onToggle={() => toggleFavorite(f.id)}
            />
            {favorites[f.id] && (
              <button
                onClick={() => toggleFavorite(f.id)}
                style={{
                  marginLeft: "0.5rem",
                  color: "red",
                  cursor: "pointer",
                  border: "none",
                  background: "transparent",
                }}
              >
                „ÅäÊ∞ó„Å´ÂÖ•„ÇäËß£Èô§
              </button>
            )}
          </div>

          <StarRating
            count={5}
            value={ratings[f.id] || 0}
            onRate={(rate) => handleRate(f.id, rate)}
          />

          <div style={{ marginTop: "1rem" }}>
            <input
              type="text"
              placeholder="„Ç≥„É°„É≥„Éà„ÇíËøΩÂä†"
              value={commentInputs[f.id] || ""}
              onChange={(e) =>
                setCommentInputs((prev) => ({ ...prev, [f.id]: e.target.value }))
              }
              onKeyDown={(e) => {
                if (e.key === "Enter")
                  handleAddComment(f.id, commentInputs[f.id] || "");
              }}
              style={{ width: "100%", padding: "0.5rem" }}
            />
            <ul
              style={{ listStyle: "none", padding: 0, marginTop: "0.5rem" }}
            >
              {(comments[f.id] || []).map((c) => {
                const isEditing = editId === `${f.id}-${c.timestamp}`;
                return (
                  <li
                    key={c.timestamp}
                    style={{
                      display: "flex",
                      justifyContent: "space-between",
                      borderBottom: "1px solid #eee",
                      padding: "0.3rem 0",
                    }}
                  >
                    <div>
                      <strong>{c.user}</strong>Ôºà{c.date}Ôºâ:
                      {isEditing ? (
                        <input
                          value={editText}
                          onChange={(e) => setEditText(e.target.value)}
                        />
                      ) : (
                        <> {c.text}</>
                      )}
                    </div>
                    {c.user === username && (
                      <div>
                        {isEditing ? (
                          <button onClick={() => handleSaveEdit(f.id, c.timestamp)}>
                            ‰øùÂ≠ò
                          </button>
                        ) : (
                          <>
                            <button
                              onClick={() => handleEditComment(f.id, c.timestamp)}
                            >
                              Á∑®ÈõÜ
                            </button>
                            <button
                              onClick={() => handleDeleteComment(f.id, c.timestamp)}
                              style={{
                                marginLeft: "0.3rem",
                                color: "red",
                              }}
                            >
                              ÂâäÈô§
                            </button>
                          </>
                        )}
                      </div>
                    )}
                  </li>
                );
              })}
            </ul>
          </div>
        </div>
      ))}
    </div>
  );
}
