// FestivalPage.js
import React, { useState, useEffect } from "react";
import StarRating from "./StarRating";

// 仮のフェス情報
const festivals = [
  { id: 1, name: "Summer Fest" },
  { id: 2, name: "Music Fiesta" },
  { id: 3, name: "Food Carnival" }
];

export default function FestivalPage() {
  // 初期値を localStorage から読み込む
  const [ratings, setRatings] = useState(() => {
    const saved = localStorage.getItem("festivalRatings");
    if (saved) {
      return JSON.parse(saved);
    }
    return festivals.reduce((acc, f) => ({ ...acc, [f.id]: 0 }), {});
  });

  // ratings が変わったら localStorage に保存
  useEffect(() => {
    localStorage.setItem("festivalRatings", JSON.stringify(ratings));
  }, [ratings]);

  const handleRate = (id, rating) => {
    const rateValue = Number(rating); // 型を保証
    setRatings(prev => ({ ...prev, [id]: rateValue }));
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "sans-serif" }}>
      <h1>Festivals</h1>
      {festivals.map(festival => (
        <div
          key={festival.id}
          style={{
            marginBottom: "1.5rem",
            padding: "1rem",
            border: "1px solid #ccc",
            borderRadius: "8px",
            maxWidth: "400px"
          }}
        >
          <h2>{festival.name}</h2>
          <p>Rating: {ratings[festival.id] ?? 0}</p>
          <StarRating
            count={5}
            value={ratings[festival.id] ?? 0}
            onRate={rate => handleRate(festival.id, rate)}
          />
        </div>
      ))}
    </div>
  );
}
