import React, { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";

export default function AccountPage() {
  const [user, setUser] = useState(null);
  const [favorites, setFavorites] = useState([]);
  const [comments, setComments] = useState([]);
  const navigate = useNavigate();
  const alertShown = useRef(false);

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("loggedInUser"));

    if (!storedUser && !alertShown.current) {
      alert("ログインが必要です。");
      alertShown.current = true;
      navigate("/");
      return;
    }
    if (!storedUser) return;

    setUser(storedUser);

    const fav = JSON.parse(localStorage.getItem(`festivalFavorites_${storedUser.username}`)) || {};
    const com = JSON.parse(localStorage.getItem(`festivalComments_${storedUser.username}`)) || {};

    // favorites は object → array
    const favList = Object.entries(fav)
      .filter(([_, v]) => v)
      .map(([id]) => id);
    const allComments = Object.values(com).flat();

    setFavorites(favList);
    setComments(allComments);
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("loggedInUser");
    alert("ログアウトしました。");
    navigate("/");
  };

  if (!user) return null;

  return (
    <div style={{ padding: "20px" }}>
      <h1>マイページ</h1>
      <p>
        ログイン中のユーザー: <strong>{user.username}</strong>
      </p>
      <button
        onClick={handleLogout}
        style={{
          background: "#f55",
          color: "white",
          border: "none",
          padding: "8px 16px",
          borderRadius: "8px",
          cursor: "pointer",
          marginBottom: "20px",
        }}
      >
        ログアウト
      </button>

      <h2>お気に入り</h2>
      {favorites.length > 0 ? (
        <ul>
          {favorites.map((id) => (
            <li key={id}>お祭りID: {id}</li>
          ))}
        </ul>
      ) : (
        <p>お気に入りはありません。</p>
      )}

      <h2>コメント一覧</h2>
      {comments.length > 0 ? (
        <ul>
          {comments.map((c) => (
            <li key={c.timestamp}>
              <strong>{c.user}</strong>（{c.date}）: {c.text}
            </li>
          ))}
        </ul>
      ) : (
        <p>コメントはまだありません。</p>
      )}
    </div>
  );
}
