import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function AccountPage() {
  const [user, setUser] = useState(null);
  const [favorites, setFavorites] = useState([]);
  const [comments, setComments] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("loggedInUser"));
    if (!storedUser) {
      alert("ログインが必要です。");
      navigate("/"); // ログインページに移動
      return;
    }

    setUser(storedUser);

    // お気に入り・コメントデータの取得
    const storedFavorites =
      JSON.parse(localStorage.getItem(`favorites_${storedUser.username}`)) || [];
    const storedComments =
      JSON.parse(localStorage.getItem(`comments_${storedUser.username}`)) || [];

    setFavorites(storedFavorites);
    setComments(storedComments);
  }, [navigate]);

  // お気に入り解除
  const handleRemoveFavorite = (festivalId) => {
    if (!user) return;
    const updatedFavorites = favorites.filter((f) => f.id !== festivalId);
    setFavorites(updatedFavorites);
    localStorage.setItem(
      `favorites_${user.username}`,
      JSON.stringify(updatedFavorites)
    );
  };

  // コメント削除
  const handleDeleteComment = (timestamp) => {
    if (!user) return;
    const updatedComments = comments.filter((c) => c.timestamp !== timestamp);
    setComments(updatedComments);
    localStorage.setItem(
      `comments_${user.username}`,
      JSON.stringify(updatedComments)
    );
  };

  // コメント編集
  const handleEditComment = (timestamp) => {
    const target = comments.find((c) => c.timestamp === timestamp);
    const newText = prompt("コメントを編集:", target.text);
    if (newText && newText.trim()) {
      const updated = comments.map((c) =>
        c.timestamp === timestamp ? { ...c, text: newText } : c
      );
      setComments(updated);
      localStorage.setItem(
        `comments_${user.username}`,
        JSON.stringify(updated)
      );
    }
  };

  // ログアウト
  const handleLogout = () => {
    localStorage.removeItem("loggedInUser");
    alert("ログアウトしました。");
    navigate("/"); // ログインページへ戻る
  };

  if (!user) {
    return null; // ログイン前は画面を描画しない
  }

  return (
    <div style={{ padding: "20px" }}>
      <h1>マイページ</h1>
      <p>ログイン中のユーザー: <strong>{user.username}</strong></p>

      <button
        onClick={handleLogout}
        style={{
          background: "#f55",
          color: "white",
          border: "none",
          padding: "8px 16px",
          borderRadius: "8px",
          cursor: "pointer",
          marginBottom: "20px",
        }}
      >
        ログアウト
      </button>

      <h2>お気に入り一覧</h2>
      {favorites.length > 0 ? (
        <ul>
          {favorites.map((f) => (
            <li key={f.id} style={{ marginBottom: "10px" }}>
              {f.name}
              <button
                onClick={() => handleRemoveFavorite(f.id)}
                style={{
                  marginLeft: "10px",
                  background: "#ccc",
                  border: "none",
                  borderRadius: "6px",
                  cursor: "pointer",
                  padding: "4px 8px",
                }}
              >
                解除
              </button>
            </li>
          ))}
        </ul>
      ) : (
        <p>お気に入りはまだありません。</p>
      )}

      <h2>コメント一覧</h2>
      {comments.length > 0 ? (
        <ul>
          {comments.map((c) => (
            <li key={c.timestamp} style={{ marginBottom: "10px" }}>
              <p>
                <strong>{c.user}</strong> ({new Date(c.timestamp).toLocaleString()})
              </p>
              <p>{c.text}</p>
              <button
                onClick={() => handleEditComment(c.timestamp)}
                style={{
                  background: "#4caf50",
                  color: "white",
                  border: "none",
                  borderRadius: "6px",
                  cursor: "pointer",
                  padding: "4px 8px",
                  marginRight: "6px",
                }}
              >
                編集
              </button>
              <button
                onClick={() => handleDeleteComment(c.timestamp)}
                style={{
                  background: "#f44336",
                  color: "white",
                  border: "none",
                  borderRadius: "6px",
                  cursor: "pointer",
                  padding: "4px 8px",
                }}
              >
                削除
              </button>
            </li>
          ))}
        </ul>
      ) : (
        <p>コメントはまだありません。</p>
      )}
    </div>
  );
}
