import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

export default function AccountPage() {
  const [user, setUser] = useState(null);
  const [favorites, setFavorites] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("currentUser"));
    if (!storedUser) {
      alert("ログインしてください");
      navigate("/");
    } else {
      setUser(storedUser);
      const userFavorites = JSON.parse(localStorage.getItem(`favorites_${storedUser.username}`)) || [];
      setFavorites(userFavorites);
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("currentUser");
    alert("ログアウトしました");
    navigate("/");
  };

  const handleDeleteAccount = () => {
    if (!user) return;
    if (!window.confirm("本当にアカウントを削除しますか？")) return;

    localStorage.removeItem(`favorites_${user.username}`);
    localStorage.removeItem(`ratings_${user.username}`);
    localStorage.removeItem(`user_${user.username}`);
    localStorage.removeItem("currentUser");

    alert("アカウントを削除しました");
    navigate("/");
  };

  return (
    <div className="account-page">
      <h1>マイページ</h1>
      {user && (
        <>
          <p>ユーザー名: {user.username}</p>
          <button onClick={handleLogout}>ログアウト</button>
          <button onClick={handleDeleteAccount}>アカウント削除</button>

          <h2>お気に入りのお祭り</h2>
          {favorites.length === 0 ? (
            <p>お気に入りはありません。</p>
          ) : (
            <ul>
              {favorites.map((id) => (
                <li key={id}>お祭りID: {id}</li>
              ))}
            </ul>
          )}
        </>
      )}
    </div>
  );
}
