import React, { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";

export default function AccountPage() {
  const [user, setUser] = useState(null);
  const [favorites, setFavorites] = useState([]);
  const [comments, setComments] = useState([]);
  const navigate = useNavigate();
  const alertShown = useRef(false); // üîπ „Ç¢„É©„Éº„Éà„ÅåÂá∫„Åü„Åã„Å©„ÅÜ„Åã„ÇíË®òÈå≤

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("loggedInUser"));

    // üîπ „Ç¢„É©„Éº„Éà„Åå„Åæ„Å†Âá∫„Å¶„ÅÑ„Å™„ÅÑÂ†¥Âêà„ÅÆ„ÅøÂÆüË°å
    if (!storedUser && !alertShown.current) {
      alert("„É≠„Ç∞„Ç§„É≥„ÅåÂøÖË¶Å„Åß„Åô„ÄÇ");
      alertShown.current = true; // „Ç¢„É©„Éº„ÉàÊ∏à„Åø„Éï„É©„Ç∞„ÇíÁ´ã„Å¶„Çã
      navigate("/"); // „É≠„Ç∞„Ç§„É≥„Éö„Éº„Ç∏„Å´ÁßªÂãï
      return;
    }

    if (!storedUser) return; // 2ÂõûÁõÆ‰ª•Èôç„ÅØ‰Ωï„ÇÇ„Åó„Å™„ÅÑ

    setUser(storedUser);

    const storedFavorites =
      JSON.parse(localStorage.getItem(`favorites_${storedUser.username}`)) || [];
    const storedComments =
      JSON.parse(localStorage.getItem(`comments_${storedUser.username}`)) || [];

    setFavorites(storedFavorites);
    setComments(storedComments);
  }, [navigate]);

  // „ÅäÊ∞ó„Å´ÂÖ•„ÇäËß£Èô§
  const handleRemoveFavorite = (festivalId) => {
    if (!user) return;
    const updatedFavorites = favorites.filter((f) => f.id !== festivalId);
    setFavorites(updatedFavorites);
    localStorage.setItem(
      `favorites_${user.username}`,
      JSON.stringify(updatedFavorites)
    );
  };

  // „Ç≥„É°„É≥„ÉàÂâäÈô§
  const handleDeleteComment = (timestamp) => {
    if (!user) return;
    const updatedComments = comments.filter((c) => c.timestamp !== timestamp);
    setComments(updatedComments);
    localStorage.setItem(
      `comments_${user.username}`,
      JSON.stringify(updatedComments)
    );
  };

  // „Ç≥„É°„É≥„ÉàÁ∑®ÈõÜ
  const handleEditComment = (timestamp) => {
    const target = comments.find((c) => c.timestamp === timestamp);
    const newText = prompt("„Ç≥„É°„É≥„Éà„ÇíÁ∑®ÈõÜ:", target.text);
    if (newText && newText.trim()) {
      const updated = comments.map((c) =>
        c.timestamp === timestamp ? { ...c, text: newText } : c
      );
      setComments(updated);
      localStorage.setItem(
        `comments_${user.username}`,
        JSON.stringify(updated)
      );
    }
  };

  // „É≠„Ç∞„Ç¢„Ç¶„Éà
  const handleLogout = () => {
    localStorage.removeItem("loggedInUser");
    alert("„É≠„Ç∞„Ç¢„Ç¶„Éà„Åó„Åæ„Åó„Åü„ÄÇ");
    navigate("/");
  };

  if (!user) {
    return null;
  }

  return (
    <div style={{ padding: "20px" }}>
      <h1>„Éû„Ç§„Éö„Éº„Ç∏</h1>
      <p>
        „É≠„Ç∞„Ç§„É≥‰∏≠„ÅÆ„É¶„Éº„Ç∂„Éº: <strong>{user.username}</strong>
      </p>

      <button
        onClick={handleLogout}
        style={{
          background: "#f55",
          color: "white",
          border: "none",
          padding: "8px 16px",
          borderRadius: "8px",
          cursor: "pointer",
          marginBottom: "20px",
        }}
      >
        „É≠„Ç∞„Ç¢„Ç¶„Éà
      </button>

      <h2>„ÅäÊ∞ó„Å´ÂÖ•„Çä‰∏ÄË¶ß</h2>
      {favorites.length > 0 ? (
        <ul>
          {favorites.map((f) => (
            <li key={f.id} style={{ marginBottom: "10px" }}>
              {f.name}
              <button
                onClick={() => handleRemoveFavorite(f.id)}
                style={{
                  marginLeft: "10px",
                  background: "#ccc",
                  border: "none",
                  borderRadius: "6px",
                  cursor: "pointer",
                  padding: "4px 8px",
                }}
              >
                Ëß£Èô§
              </button>
            </li>
          ))}
        </ul>
      ) : (
        <p>„ÅäÊ∞ó„Å´ÂÖ•„Çä„ÅØ„Åæ„Å†„ÅÇ„Çä„Åæ„Åõ„Çì„ÄÇ</p>
      )}

      <h2>„Ç≥„É°„É≥„Éà‰∏ÄË¶ß</h2>
      {comments.length > 0 ? (
        <ul>
          {comments.map((c) => (
            <li key={c.timestamp} style={{ marginBottom: "10px" }}>
              <p>
                <strong>{c.user}</strong>Ôºà
                {new Date(c.timestamp).toLocaleString()}Ôºâ
              </p>
              <p>{c.text}</p>
              <button
                onClick={() => handleEditComment(c.timestamp)}
                style={{
                  background: "#4caf50",
                  color: "white",
                  border: "none",
                  borderRadius: "6px",
                  cursor: "pointer",
                  padding: "4px 8px",
                  marginRight: "6px",
                }}
              >
                Á∑®ÈõÜ
              </button>
              <button
                onClick={() => handleDeleteComment(c.timestamp)}
                style={{
                  background: "#f44336",
                  color: "white",
                  border: "none",
                  borderRadius: "6px",
                  cursor: "pointer",
                  padding: "4px 8px",
                }}
              >
                ÂâäÈô§
              </button>
            </li>
          ))}
        </ul>
      ) : (
        <p>„Ç≥„É°„É≥„Éà„ÅØ„Åæ„Å†„ÅÇ„Çä„Åæ„Åõ„Çì„ÄÇ</p>
      )}
    </div>
  );
}
