import React, { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { festivals } from "./FestivalData";

export default function AccountPage() {
  const [user, setUser] = useState(null);
  const [favorites, setFavorites] = useState({});
  const [ratings, setRatings] = useState({});
  const [comments, setComments] = useState({});
  const [editText, setEditText] = useState("");
  const [editId, setEditId] = useState(null);
  const alertShown = useRef(false);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("loggedInUser"));
    if (!storedUser && !alertShown.current) {
      alert("„É≠„Ç∞„Ç§„É≥„ÅåÂøÖË¶Å„Åß„Åô„ÄÇ");
      alertShown.current = true;
      navigate("/");
      return;
    }
    if (!storedUser) return;

    setUser(storedUser);
    const fav = JSON.parse(localStorage.getItem(`festivalFavorites_${storedUser.username}`)) || {};
    const rate = JSON.parse(localStorage.getItem(`festivalRatings_${storedUser.username}`)) || {};
    const com = JSON.parse(localStorage.getItem("festivalComments")) || {};

    setFavorites(fav);
    setRatings(rate);
    setComments(com);
  }, [navigate]);

  const saveData = () => {
    if (!user) return;
    localStorage.setItem(`festivalFavorites_${user.username}`, JSON.stringify(favorites));
    localStorage.setItem(`festivalRatings_${user.username}`, JSON.stringify(ratings));
    localStorage.setItem("festivalComments", JSON.stringify(comments));
  };

  // „ÅäÊ∞ó„Å´ÂÖ•„ÇäËß£Èô§
  const removeFavorite = (id) => {
    const updated = { ...favorites, [id]: false };
    setFavorites(updated);
    saveData();
  };

  // Ë©ï‰æ°Â§âÊõ¥
  const changeRating = (id, value) => {
    const updated = { ...ratings, [id]: Number(value) };
    setRatings(updated);
    saveData();
  };

  // „Ç≥„É°„É≥„ÉàÂâäÈô§
  const deleteComment = (fid, ts) => {
    const updated = {
      ...comments,
      [fid]: comments[fid].filter((c) => c.timestamp !== ts),
    };
    setComments(updated);
    saveData();
  };

  // „Ç≥„É°„É≥„ÉàÁ∑®ÈõÜ
  const editComment = (fid, ts) => {
    setEditId(`${fid}-${ts}`);
    const t = comments[fid].find((c) => c.timestamp === ts);
    setEditText(t.text);
  };

  const saveCommentEdit = (fid, ts) => {
    const updated = {
      ...comments,
      [fid]: comments[fid].map((c) =>
        c.timestamp === ts
          ? { ...c, text: editText, date: new Date().toLocaleString() }
          : c
      ),
    };
    setComments(updated);
    setEditId(null);
    setEditText("");
    saveData();
  };

  const handleLogout = () => {
    localStorage.removeItem("loggedInUser");
    alert("„É≠„Ç∞„Ç¢„Ç¶„Éà„Åó„Åæ„Åó„Åü„ÄÇ");
    navigate("/");
  };

  if (!user) return null;

  const myComments = Object.entries(comments)
    .flatMap(([fid, list]) => list.filter((c) => c.user === user.username).map((c) => ({ ...c, fid })));

  return (
    <div style={{ padding: "2rem", fontFamily: "sans-serif" }}>
      <h1>„Éû„Ç§„Éö„Éº„Ç∏</h1>
      <p>„É≠„Ç∞„Ç§„É≥‰∏≠: <strong>{user.username}</strong></p>

      <button onClick={handleLogout} style={{ background: "#f55", color: "white", border: "none", padding: "8px 16px", borderRadius: "8px" }}>
        „É≠„Ç∞„Ç¢„Ç¶„Éà
      </button>

      <h2>‚≠ê Ë©ï‰æ°Ê∏à„Åø„ÅÆ„ÅäÁ•≠„Çä</h2>
      <ul>
        {Object.entries(ratings)
          .filter(([_, v]) => v > 0)
          .map(([fid, value]) => {
            const f = festivals.find((x) => x.id === Number(fid));
            return (
              <li key={fid}>
                {f?.name || `ID:${fid}`} ‚Äî {value}‚òÖ{" "}
                <button onClick={() => changeRating(fid, prompt("Êñ∞„Åó„ÅÑÊòü(1-5):", value))}>Â§âÊõ¥</button>
              </li>
            );
          })}
      </ul>

      <h2>üíñ „ÅäÊ∞ó„Å´ÂÖ•„Çä„ÅÆ„ÅäÁ•≠„Çä</h2>
      <ul>
        {Object.entries(favorites)
          .filter(([_, v]) => v)
          .map(([fid]) => {
            const f = festivals.find((x) => x.id === Number(fid));
            return (
              <li key={fid}>
                {f?.name || `ID:${fid}`}{" "}
                <button onClick={() => removeFavorite(fid)} style={{ color: "red" }}>Ëß£Èô§</button>
              </li>
            );
          })}
      </ul>

      <h2>üí¨ Ëá™ÂàÜ„ÅÆ„Ç≥„É°„É≥„Éà</h2>
      <ul>
        {myComments.length > 0 ? (
          myComments.map((c) => {
            const isEditing = editId === `${c.fid}-${c.timestamp}`;
            const fest = festivals.find((f) => f.id === Number(c.fid));
            return (
              <li key={c.timestamp}>
                <strong>{fest?.name}</strong> ‚Äî {c.date}
                <br />
                {isEditing ? (
                  <>
                    <input value={editText} onChange={(e) => setEditText(e.target.value)} />
                    <button onClick={() => saveCommentEdit(c.fid, c.timestamp)}>‰øùÂ≠ò</button>
                  </>
                ) : (
                  <>
                    {c.text}{" "}
                    <button onClick={() => editComment(c.fid, c.timestamp)}>Á∑®ÈõÜ</button>
                    <button onClick={() => deleteComment(c.fid, c.timestamp)} style={{ color: "red" }}>
                      ÂâäÈô§
                    </button>
                  </>
                )}
              </li>
            );
          })
        ) : (
          <p>„Ç≥„É°„É≥„Éà„ÅØ„Åæ„Å†„ÅÇ„Çä„Åæ„Åõ„Çì„ÄÇ</p>
        )}
      </ul>
    </div>
  );
}
